export type Prettify<T> = {
    [k in keyof T]: T[k];
} & {};
/**
 * Util type decrement the number. Supports number upto 9. Helps in preventing
 * excessive recursion when using recursive types and no type information is passed
 */
export type Decrement<S extends string> = S extends '9' ? '8' : S extends '8' ? '7' : S extends '7' ? '6' : S extends '6' ? '5' : S extends '5' ? '4' : S extends '4' ? '3' : S extends '3' ? '2' : S extends '2' ? '1' : never;
